CREATE DATABASE railway_new_db;
USE railway_new_db;

CREATE TABLE station(
	station_id int NOT NULL IDENTITY(1,1) PRIMARY KEY,
	station_name varchar(255) NOT NULL,
	station_location varchar(255) NOT NULL,
	UNIQUE(station_name, station_location),
);

CREATE TABLE track(
	track_id int NOT NULL IDENTITY(1,1) PRIMARY KEY,
	from_station int FOREIGN KEY REFERENCES station(station_id),
	to_statiton int FOREIGN KEY REFERENCES station(station_id),
	distance_in_km int NOT NULL,
	UNIQUE(from_station, to_statiton),
);

CREATE TABLE train(
	train_id int NOT NULL IDENTITY(100000,100000) PRIMARY KEY,
	train_name varchar(255) NOT NULL,
	train_type varchar(255) NOT NULL,
);

CREATE TABLE schedule(
	station_id int FOREIGN KEY REFERENCES station(station_id),
	train_id int FOREIGN KEY REFERENCES train(train_id),
	time_in TIME(0),
	time_out TIME(0),
	back_time_in TIME(0),
	back_time_out TIME(0),
	sequence_number int,
	PRIMARY KEY(station_id, train_id),
);

CREATE TABLE passenger(
	passenger_id int NOT NULL IDENTITY(1000,1) PRIMARY KEY,
    user_name varchar(255) NOT NULL UNIQUE,
    password varchar(255),
	first_name varchar(255) NOT NULL,
	last_name varchar(255) NOT NULL,
	identification char(9) NOT NULL UNIQUE CHECK(identification NOT LIKE '%[^0-9]%'),
    status VARCHAR(255) DEFAULT NULL,
    is_admin BIT NOT NULL DEFAULT 0,
--     cash NUMERIC(9, 2) DEFAULT 0,
);

CREATE TABLE coach(
	coach_id int NOT NULL PRIMARY KEY,
	train_id int FOREIGN KEY REFERENCES train(train_id),
);

CREATE TABLE seat(
	seat_id int NOT NULL PRIMARY KEY,
	coach_id int FOREIGN KEY REFERENCES coach(coach_id),
);

CREATE TABLE trip (
	trip_id CHAR(6) NOT NULL PRIMARY KEY,
	from_station INT NOT NULL FOREIGN KEY REFERENCES station(station_id),
	to_station INT NOT NULL FOREIGN KEY REFERENCES station(station_id),
	price INT NOT NULL,
	UNIQUE(from_station, to_station),
);

CREATE TABLE ticket(
	ticket_id int NOT NULL IDENTITY(1000,1) PRIMARY KEY,
	depature_date date,
	seat_id int UNIQUE FOREIGN KEY REFERENCES seat(seat_id),
    trip CHAR(6) FOREIGN KEY REFERENCES trip(trip_id),
    reserve BIT NOT NULL DEFAULT 0,
    insured BIT NOT NULL DEFAULT 0,
    UNIQUE(depature_date, seat_id),
);
CREATE TABLE INSURANCE(
    insurance_id INT IDENTITY(1,1) PRIMARY KEY,
    TICKET_ID INT FOREIGN KEY REFERENCES TICKET(TICKET_ID),
);

CREATE TABLE pas_cash(
	passenger_id int FOREIGN KEY REFERENCES passenger(passenger_id),
	cash NUMERIC(9, 2) DEFAULT 0,
	change VARCHAR(1) DEFAULT '-',
)

CREATE TABLE passenger_ticket(
	passenger_id int FOREIGN KEY REFERENCES passenger(passenger_id),
	ticket_id int UNIQUE FOREIGN KEY REFERENCES ticket(ticket_id),
    price INT,
    discount_persentage INT DEFAULT 0,
);

CREATE TABLE discount(
	discount_id INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
	passenger_id INT FOREIGN KEY REFERENCES passenger(passenger_id) UNIQUE,
	cnt_trips INT DEFAULT -1,
	expiration_date DATE,
);

CREATE TABLE train_delay(
	train_delay_id int NOT NULL IDENTITY(1,1) PRIMARY KEY,
	station_1 int,
    station_2 int,
	train_id int FOREIGN KEY REFERENCES train(train_id),
	train_delay_time TIME(0),
    transport_company_fault VARCHAR(255),
	direction VARCHAR(255),
    reason VARCHAR(255),
);

CREATE TABLE delay_passengers (
	train_name VARCHAR(255),
	delay_time TIME(0),
	transport_company_fault VARCHAR(255),
	first_name VARCHAR(255),
	last_name VARCHAR(255),
	identification INT,
	ticket_id INT UNIQUE WITH (IGNORE_DUP_KEY = ON),
	price NUMERIC(9,2),
	compensation NUMERIC(9, 2) DEFAULT 0,
);

CREATE TABLE STATION_TIME(
	STATION_NAME VARCHAR(20),
	TIME_ARR TIME(0),
	TIME_DEP TIME(0),
    SEQ_NUM INT,
);

CREATE TABLE penalty(
    penalty_id int identity(1,1),
    passenger_id int,
    penalty_description VARCHAR(255),
    penalty_price int,
    penalty_status VARCHAR(255),
    PRIMARY KEY(penalty_id),
    FOREIGN KEY (passenger_id) REFERENCES passenger(passenger_id)
);

CREATE TABLE review(
    review_id int identity(1,1),
    review_text VARCHAR(255),
    review_stars int,
    passenger_id int,
    review_desc VARCHAR(255),
    PRIMARY KEY (review_id),
    FOREIGN KEY (passenger_id) REFERENCES passenger(passenger_id)
);

CREATE TABLE lost_items(
    lost_item_id int identity(1,1),
    lost_item_name VARCHAR(255),
    lost_item_description VARCHAR(255),
    lost_item_status VARCHAR(255),
    lost_item_weight int,
    lost_item_repayment int,
    PRIMARY KEY(lost_item_id)
);

